import DropdownButton from 'react-bootstrap/DropdownButton';
import { CaretDownFill } from 'react-bootstrap-icons';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import FormControl from 'react-bootstrap/FormControl';
import Dropdown from 'react-bootstrap/Dropdown';
import React, { useState } from 'react';

// The forwardRef is important!!
// Dropdown needs access to the DOM node in order to position the Menu
const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
  <a
    href=""
    ref={ref}
    onClick={(e) => {
      e.preventDefault();
      onClick(e);
    }}
  >
    {children}
    &nbsp;
    <CaretDownFill/>
  </a>
));

// forwardRef again here!
// Dropdown needs access to the DOM of the Menu to measure it
const CustomMenu = React.forwardRef(
  ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
    const [value, setValue] = useState('');

    return (
      <div
        ref={ref}
        style={style}
        className={className}
        aria-labelledby={labeledBy}
      >
        <FormControl
          autoFocus
          className="mx-3 my-2 w-auto"
          placeholder="Type to filter..."
          onChange={(e) => setValue(e.target.value)}
          value={value}
        />
        <ul className="list-unstyled">
          {React.Children.toArray(children).filter(
            (child) =>
              !value || child.props.children.toLowerCase().startsWith(value),
          )}
        </ul>
      </div>
    );
  },
);

const filters = ['Region','Quantity','Price'];
const variants = [];

export default function DynamicFilters(){
    return(
        <div className="DynamicFilters">
            {variants.map(
                (variant, idx) => (
                    <DropdownButton
                        as={ButtonGroup}
                        key={variant}
                        id={`dropdown-variants-${variant}`}
                        variant={`outline-${variant.toLowerCase()}`}
                        title={filters[idx]}
                        block
                    >
                        <Dropdown.Item eventKey="1">Action</Dropdown.Item>
                        <Dropdown.Item eventKey="2">Another action</Dropdown.Item>
                        <Dropdown.Item eventKey="3" active>
                        Active Item
                        </Dropdown.Item>
                        <Dropdown.Divider />
                        <Dropdown.Item eventKey="4">Separated link</Dropdown.Item>
                    </DropdownButton>
                ),
            )}
            <Dropdown
                id={`dropdown-variants-filter`}
                variant={`outline-dark`}
                title='Filter'
                className='dds'
                block
            >
                <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components">
                    Filter by...
                </Dropdown.Toggle>
                <Dropdown.Menu as={CustomMenu}>
                    <Dropdown.Item eventKey="region">Region</Dropdown.Item>
                    <Dropdown.Item eventKey="quantity">Quantity</Dropdown.Item>
                    <Dropdown.Item eventKey="price">Price</Dropdown.Item>
                </Dropdown.Menu>
            </Dropdown>
        </div>
    );
}
