import 'react-bootstrap-table-next/dist/react-bootstrap-table2.min.css';
import BootstrapTable from 'react-bootstrap-table-next';
import CardColumns from 'react-bootstrap/CardColumns';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import '../stylesheets/SupplyList.css';
import SupplyForm from './SupplyForm.js';
import Card from 'react-bootstrap/Card';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const supplies = [
    {
        index: 1,
        name: 'Test Card',
        address: 'Test address',
        description: 'Test description',
        amount: 1000
    },
    {
        index: 2,
        name: 'Test Card',
        address: 'Test address',
        description: 'Test description',
        amount: 3000
    },
    {
        index: 3,
        name: 'Test Card',
        address: 'Test address',
        description: 'Test description',
        amount: 2000
    }
]
const columns = [
    {
        dataField: 'index',
        text: 'Supply Index'
    },
    {
        dataField: 'name',
        text: 'Supply Name'
    },
    {
        dataField: 'address',
        text: 'Supply Address'
    },
    {
        dataField: 'description',
        text: 'Supply Description'
    },
    {
        dataField: 'amount',
        text: 'Supply Amount',
        sort: true
    }
]
const options = {
    custom: true,
    totalSize: supplies.list
}

var supplyList=supplies.map((supply)=>{
    var totalPrice = 1000;
    return(
        <Card  key={supply.index}>
            <Card.Body>
                <Card.Title>Item Name: {supply.name}
                <div style={{textAlign:"right"}}>
                    <Button onClick={()=>console.log(supply.index)} variant = "outline-danger">
                    <svg class="bi bi-trash" width="1em" height="1em" viewBox="0 0 16 16" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                    <path d="M5.5 5.5A.5.5 0 016 6v6a.5.5 0 01-1 0V6a.5.5 0 01.5-.5zm2.5 0a.5.5 0 01.5.5v6a.5.5 0 01-1 0V6a.5.5 0 01.5-.5zm3 .5a.5.5 0 00-1 0v6a.5.5 0 001 0V6z"/>
                    <path fill-rule="evenodd" d="M14.5 3a1 1 0 01-1 1H13v9a2 2 0 01-2 2H5a2 2 0 01-2-2V4h-.5a1 1 0 01-1-1V2a1 1 0 011-1H6a1 1 0 011-1h2a1 1 0 011 1h3.5a1 1 0 011 1v1zM4.118 4L4 4.059V13a1 1 0 001 1h6a1 1 0 001-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z" clip-rule="evenodd"/>
                    </svg>
                    </Button>
                </div>
                </Card.Title>
                <Card.Subtitle>Address: {supply.address}</Card.Subtitle>
                <hr/>
                <p>Description</p>
                <p className="supply-description">{supply.description}</p>
                <Card.Subtitle>{supply.amount} for Php{totalPrice}</Card.Subtitle>
            </Card.Body>
        </Card>
    );
});

export default function SupplyList(){
    return(
        <div className="SupplyList">
            <div>
                <Jumbotron className="text-center">
                   <h1>Supply List</h1>
                </Jumbotron>
            </div>
            <Container>
                <Card>
                    <Card.Body>
                        <BootstrapTable
                          keyField='index'
                          data={ supplies }
                          columns={ columns }
                          bordered={ false } />
                    </Card.Body>
                </Card>
            </Container>
        </div>
    );
}
