import React, { useState } from 'react';
import {useForm, useStep} from 'react-hooks-helper';
import Jumbotron from 'react-bootstrap/Jumbotron';
import OrgForm from './OrgForm.js';
import SupplyForm from './SupplyForm.js';
import Button from 'react-bootstrap/Button';
import { Link } from 'react-router-dom';

const steps = [
    { id: "organization" },
    { id: "supplies" },
    { id: "submit" }
  ];
const defaultData={
    orgName:'Organization',
    firstName: 'Juan',
    lastName: 'Dela Cruz',
    email: 'jdelacruz@domain.com',
    phone: 9261234567,
    supplies:{}
}

export default function AddSupply(){
    const {step, navigation} = useStep({ initialStep: 0, steps });
    /* const [formData, setForm] = useForm(defaultData); */
   /*  const props2 = { formData, setForm, navigation }; */
    const [orgName, setOrgName] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState(9261234567); 
    const props = {orgName, setOrgName, firstName, setFirstName, lastName, setLastName, email, setEmail, phone, setPhone,navigation};

    const [unit, setUnit] = useState('');
    const [formName, setFormName] = useState('');
    const [formAddress, setFormAddress] = useState('');
    const [formAmount, setFormAmount] = useState(1);
    const [formPrice, setFormPrice] = useState(0.00);
    const [formDescription, setFormDescription] = useState('');
    const [supplies, setSupplies] = useState([]);
    const [formRegion, setRegion] = useState('');
    const props2={unit, setUNit, formName, setFormName, formAddress, setFormAddress, formAmount, setFormAmount, formPrice, setFOrmPRice,
    formDescription, setFormDescription, supplies, setSupplies, formRegion, setRegion, navigation}


    const { id } = step;
    switch(id){
        case "organization":
            return <OrgForm {...props}/>;
        case "supplies":
            return <SupplyForm {...props}/>
        case "submit":
            return(
                <div>
                    <Jumbotron>
                        <h1>Thank you for your donation!</h1>
                        <p>Thanks for helping out our LGUs. Please keep your email and phone at the ready if an LGU would like to purchase your supplies.</p>
                        <Link to="/"><Button>Back To Home</Button></Link>
                    </Jumbotron>
                </div>
            );
        default:
            return null;
    }
}
