import filterFactory, {
  textFilter,
  numberFilter,
} from "react-bootstrap-table2-filter";
import "react-bootstrap-table-next/dist/react-bootstrap-table2.min.css";
import paginationFactory from "react-bootstrap-table2-paginator";
import BootstrapTable from "react-bootstrap-table-next";
import CardColumns from "react-bootstrap/CardColumns";
import Jumbotron from "react-bootstrap/Jumbotron";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import "../stylesheets/SupplyList.css";
import SupplyForm from "./SupplyForm.js";
import Card from "react-bootstrap/Card";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";

const supplies = [
  {
    index: 1,
    supplier: "Test supplier",
    name: "Test Card",
    region: "National Capital Region",
    address: "Test address",
    quantity: 5,
    unit: "kg",
    price: 1000,
    description: "Test description",
    is_validated: true,
  },
  {
    index: 2,
    supplier: "Test supplier",
    name: "Test Card",
    region: "National Capital Region",
    address: "Test address",
    quantity: 3,
    unit: "kg",
    price: 3000,
    description: "Test description",
    is_validated: true,
  },
  {
    index: 3,
    supplier: "Test supplier",
    name: "Test Card",
    region: "National Capital Region",
    address: "Test address",
    quantity: 4,
    unit: "kg",
    price: 2000,
    description: "Test description",
    is_validated: true,
  },
];
const columns = [
  {
    dataField: "index",
    text: "ID",
    searchable: false,
  },
  {
    dataField: "name",
    text: "Name"
  },
  {
    dataField: "region",
    text: "Region",
    sort: true,
    searchable: false,
    filter: textFilter(),
  },
  {
    dataField: "address",
    text: "Address",
    searchable: false,
  },
  {
    dataField: "quantity",
    text: "Quantity",
    sort: true,
    searchable: false,
    filter: textFilter(),
  },
  {
    dataField: "unit",
    text: "Unit",
    searchable: false,
  },
  {
    dataField: "price",
    text: "Price",
    sort: true,
    searchable: false,
    filter: textFilter(),
  },
  {
    dataField: "description",
    searchable: false,
    text: "Description",
  },
];

const pagination = paginationFactory({
  totalSize: supplies.length,
  sizePerPage: 5,
});

function SupplyList({ auth }) {
  return (
    <div className="SupplyList">
      <div>
        <Jumbotron className="text-center">
          <h1>Supply List</h1>
        </Jumbotron>
      </div>
      <Container>
        <Card>
          <Card.Body>
            <BootstrapTable
              keyField="index"
              data={supplies}
              columns={columns}
              bordered={false}
              pagination={pagination}
              filter={filterFactory()}
              bootstrap4
            />
          </Card.Body>
        </Card>
      </Container>
    </div>
  );
}

SupplyList.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps)(SupplyList);
