import React, {useState} from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import '../stylesheets/formStyle.css';
import { Link } from 'react-router-dom';

import Jumbotron from 'react-bootstrap/Jumbotron';

const OrgForm = ({ orgName, firstName, lastName, email, phone, setForm, navigation })=>{
    const {next} = navigation;
    /* const [formOrgName, setOrgName] = useState('');
    const [formFirstName, setFirstName] = useState('');
    const [formLastName, setLastName] = useState('');
    const [formEmail, setEmail] = useState('');
    const [formPhone, setPhone] = useState(9261234567); */
    //const {orgName, firstName, lastName, email, phone} = formData;

    return(
        <div className ="OrgForm">
        <div>
                <Jumbotron>
                    <Link to="/"><Button variant="outline-secondary">Back to Home</Button></Link>
                   <h3>Hi, thank you for registering your produce to the site. 
                       Just answer this form and we'll be good to go!</h3> 
                </Jumbotron>
        </div>
        <div className="container">

            <Form >
                <h3>Organization Information</h3>
                <Form.Label>Organization</Form.Label>
                <Form.Group>

                    <Form.Control 
                    value={orgName}
                    onChange={e=>{setForm}}
                    type="text" placeholder="Organization Name" />
                    <Form.Text className="text-muted">
                        Answer "Independent" if under no organization.
                    </Form.Text>
                </Form.Group>
                <Form.Label>Representative</Form.Label>
                <Form.Group className="container">

                    <div className="row">
                        <Form.Control 
                        value={firstName}
                        onChange={setForm}
                        className="col-sm-4" type="text" placeholder="First Name" />
                        <Form.Control 
                        value={lastName}
                        onChange={setForm}
                        className="col-sm-4" type="text" placeholder="Last Name" />
                    </div>
                </Form.Group>
                <Form.Label>Contact Information</Form.Label>
                <Form.Group className="container">

                    <div className="row">

                    <Form.Control 
                    value={phone}
                    onChange={setForm}
                    className="col-sm-4" type="number" placeholder="Phone Number" />
                    <Form.Control 
                    value={email}
                    onChange={setForm}
                    className="col-sm-4"type="email" placeholder="Email" />
                    </div>  
                </Form.Group>

            </Form>
            <div className="container">
                <div className="row">
                <Button onClick={next} variant="info">Next
            <svg className="bi bi-arrow-right" width="1em" height="1em" viewBox="0 0 16 16" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
  <path fillRule="evenodd" d="M10.146 4.646a.5.5 0 01.708 0l3 3a.5.5 0 010 .708l-3 3a.5.5 0 01-.708-.708L12.793 8l-2.647-2.646a.5.5 0 010-.708z" clipRule="evenodd"/>
  <path fillRule="evenodd" d="M2 8a.5.5 0 01.5-.5H13a.5.5 0 010 1H2.5A.5.5 0 012 8z" clipRule="evenodd"/>
</svg>
            </Button>{' '}
                </div>
            </div>

        </div>
        </div>
    );
}

export default OrgForm;
