import axios from "axios";

import { returnErrors } from "./messages.js";

import {
  USER_LOADED,
  USER_LOADING,
  AUTH_ERROR,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  LOGOUT_SUCCESS,
  REGISTER_SUCCESS,
  REGISTER_FAIL,
} from "./types.js";

export const loadUser = () => (dispatch, getState) => {
  //User loading
  dispatch({ type: USER_LOADING });
  axios
    .get("http://localhost:8000/api/auth/user", tokenConfig(getState))
    .then((res) => {
      dispatch({
        type: USER_LOADED,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch(returnErrors(err.response.data, err.response.status));
      dispatch({
        type: AUTH_ERROR,
      });
    });
};

//LOGIN USER
export const login = (username, password) => (dispatch) => {
  console.log("hello me login");

  //headers
  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };
  //REquest Body
  const body = JSON.stringify({
    username: username,
    password: password,
  });

  axios
    .post("http://localhost:8000/api/auth/login", body, config)
    .then((res) => {
      dispatch({
        type: LOGIN_SUCCESS,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch(returnErrors(err.response.data, err.response.status));
      dispatch({
        type: LOGIN_FAIL,
      });
    });
};
//LOGOUT USER

export const logout = () => (dispatch, getState) => {
  console.log("wasCLick");
  axios
    .post("http://localhost:8000/api/auth/logout/", null, tokenConfig(getState))
    .then((res) => {
      dispatch({
        type: LOGOUT_SUCCESS,
      });
    })
    .catch((err) => {
      dispatch(returnErrors(err.response.data, err.response.status));
    });
};

// Setup config with token - helper function
export const tokenConfig = (getState) => {
  // Get token from state
  const token = getState().auth.token;

  // Headers
  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };

  // If token, add to headers config
  if (token) {
    config.headers["Authorization"] = `Token ${token}`;
  }

  return config;
};

//REGISTER
export const register = (email, username, mobile, dept, password) => (dispatch) => {
  console.log("hello me register");

  //headers
  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };
  //REquest Body
  const body = JSON.stringify({
    username: username,
    password: password,
    email,
    mobile,
    department: dept,
  });

  axios
    .post("http://localhost:8000/api/auth/register", body, config)
    .then((res) => {
      dispatch({
        type: REGISTER_SUCCESS,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch(returnErrors(err.response.data, err.response.status));
      dispatch({
        type: REGISTER_FAIL,
      });
    });
};
