import DropdownButton from 'react-bootstrap/DropdownButton';
import '../../stylesheets/SupplyList/DynamicFilters.css';
import { PlusCircle, DashCircle } from 'react-bootstrap-icons';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import Dropdown from 'react-bootstrap/Dropdown';
import React, { useState } from 'react';

const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
  <a
    href=""
    ref={ref}
    onClick={(e) => {
      e.preventDefault();
      onClick(e);
    }}
  >
    {children}
    &nbsp;
    <PlusCircle/>
  </a>
));

const CustomMenu = React.forwardRef(
  ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
    const [value, setValue] = useState('');

    return (
      <div
        ref={ref}
        style={style}
        className={className}
        aria-labelledby={labeledBy}
      >
        <ul className="list-unstyled">
          {React.Children.toArray(children).filter(
            (child) =>
              !value || child.props.children.toLowerCase().startsWith(value),
          )}
        </ul>
      </div>
    );
  },
);

const filterObject = {
    'region': 'Region',
    'quantity': 'Quantity',
    'price': 'Price'
}

export default function DynamicFilters(){
    const [currentFilters, setCurrentFilters] = useState([ 'region', 'quantity', 'price' ]);
    const [selectedFilters, setSelectedFilters] = useState([]);
    const [isAllSelectedFlag, setIsAllSelectedFlag] = useState(false);

    const onSelect = (eventKey, event) => {
        setSelectedFilters([
            ...selectedFilters,
            eventKey
        ]);
        setCurrentFilters(currentFilters.filter(item => item !== eventKey));

        if (currentFilters.length === 0) setIsAllSelectedFlag(true);
        else setIsAllSelectedFlag(false);
        console.log(isAllSelectedFlag);
    };

    return(
        <div className="DynamicFilters">
            {selectedFilters.map(
                (variant, idx) => (
                    <DropdownButton
                        as={ButtonGroup}
                        key={variant}
                        id={`dropdown-variants-${variant}`}
                        variant={`outline-${variant.toLowerCase()}`}
                        title={filterObject[variant]}
                        block
                    >
                        <Dropdown.Item eventKey="1">Action</Dropdown.Item>
                        <Dropdown.Item eventKey="2">Another action</Dropdown.Item>
                        <Dropdown.Item eventKey="3" active>
                        Active Item
                        </Dropdown.Item>
                        <Dropdown.Divider />
                        <Dropdown.Item eventKey="4">Separated link</Dropdown.Item>
                    </DropdownButton>
                ),
            )}
            {isAllSelectedFlag? <></> :
              <Dropdown
                  id={`dropdown-variants-filter`}
                  variant={`outline-dark`}
                  title='Filter'
                  className='dds'
                  onSelect= {onSelect}
              >
                  <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components">
                      Add Filter
                  </Dropdown.Toggle>
                  <Dropdown.Menu>
                      {currentFilters.map(
                          (filter, idx) => (
                              <Dropdown.Item
                                  key={`df-dd-item-${idx}`}
                                  eventKey={filter}>
                                  { filterObject[filter] }
                              </Dropdown.Item>
                          ),
                      )}
                  </Dropdown.Menu>
              </Dropdown>}
        </div>
    );
}
